def toWords(documents: Dataset[String], separatorsRegexp: String = """\s+"""): Dataset[String] = {
    documents.flatMap(doc => doc.split(separatorsRegexp))
        .map(word => word.toLowerCase)
        .filter(word => !word.isEmpty)
}

def countWords(documents: Dataset[String], separatorsRegexp: String = """\s+""") : Dataset[(String, Long)] = {
    val words = toWords(documents, separatorsRegexp)
    val counts = words.groupByKey(identity).count()
    counts
}
